src/test/ShowtimeMT.t.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity =0.8.7;

import "./Hevm.sol";
import "./TestToken.sol";
import "../../lib/ds-test/src/test.sol";
import "../ShowtimeMT.sol";
import "../ShowtimeV1Market.sol";
import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";

contract User is ERC1155Holder {}

contract ShowtimeMTAccessTest is DSTest, ERC1155Holder {
    User internal anon;
    User internal admin;
    User internal minter;
    address[] internal minters;
    ShowtimeMT internal mt;
    Hevm internal constant hevm = Hevm(HEVM_ADDRESS);

    event UserAccessSet(address _user, string _access, bool _enabled);

    function setUp() public {
        anon = new User();
        admin = new User();
        minter = new User();
        minters = [
            address(new User()),
            address(new User()),
            address(new User()),
            address(new User()),
            address(new User())
        ];

        mt = new ShowtimeMT();
        mt.setAdmin(address(admin), true);
        mt.setMinter(address(minter), true);
    }

    // it non-minter should not be able to mint
    function testNotMintersCannotMint() public {
        hevm.prank(address(anon));
        hevm.expectRevert("AccessProtected: caller is not minter");

        mt.issueToken(address(this), 10, "some-hash", "0", address(0), 0);
    }

    // it owner should be able to set admin
    function testOwnerCanSetAdmin() public {
        assertTrue(!mt.isAdmin(address(anon)));

        hevm.expectEmit(true, true, true, true);
        emit UserAccessSet(address(anon), "ADMIN", true);

        mt.setAdmin(address(anon), true);

        assertTrue(mt.isAdmin(address(anon)));
    }

    // it admin should be able to set minter
    function testAdminCanSetMinter() public {
        assertTrue(!mt.isMinter(address(anon)));

        hevm.prank(address(admin));
        mt.setMinter(address(anon), true);
        emit UserAccessSet(address(anon), "MINTER", true);

        assertTrue(mt.isMinter(address(anon)));
    }

    // it admin should be able to set batch of minters
    function testAdminCanSetMinterBatch() public {
        for (uint256 i = 0; i < minters.length; i++) {
            assertTrue(!mt.isMinter(minters[i]));
        }

        hevm.prank(address(admin));

        for (uint256 i = 0; i < minters.length; i++) {
            hevm.expectEmit(true, true, true, true);
            emit UserAccessSet(minters[i], "MINTER", true);
        }

        mt.setMinters(minters, true);

        for (uint256 i = 0; i < minters.length; i++) {
            assertTrue(mt.isMinter(minters[i]));
        }
    }

    // it admin should be able to revoke minter
    function testAdminCanRevokeMinter() public {
        assertTrue(mt.isMinter(address(minter)));

        hevm.prank(address(admin));
        hevm.expectEmit(true, true, true, true);
        emit UserAccessSet(address(minter), "MINTER", false);

        mt.setMinter(address(minter), false);

        assertTrue(!mt.isMinter(address(minter)));
    }

    // it admin should be able to revoke batch of minters
    function testAdminCanRevokeMinterBatch() public {
        hevm.prank(address(admin));

        for (uint256 i = 0; i < minters.length; i++) {
            hevm.expectEmit(true, true, true, true);
            emit UserAccessSet(minters[i], "MINTER", false);
        }

        mt.setMinters(minters, false);

        for (uint256 i = 0; i < minters.length; i++) {
            assertTrue(!mt.isMinter(minters[i]));
        }
    }

    // it non-owner should not be able to set admin
    function testNonOwnerCannotSetAdmin() public {
        hevm.expectRevert("Ownable: caller is not the owner");

        hevm.prank(address(admin));
        mt.setAdmin(address(admin), true);
    }

    // it owner should be able to revoke admin
    function testOwnerCanRevokeAdmin() public {
        hevm.expectEmit(true, true, true, true);
        emit UserAccessSet(address(admin), "ADMIN", false);

        mt.setAdmin(address(admin), false);

        assertTrue(!mt.isAdmin(address(admin)));
    }

    // it admin should be able to enable/disable minting for all
    function testAdminCanControlPublicMinting() public {
        hevm.startPrank(address(admin));
        hevm.expectEmit(true, true, true, true);
        emit UserAccessSet(address(0), "MINTER", true);

        mt.setPublicMinting(true);

        assertTrue(mt.publicMinting());

        hevm.expectEmit(true, true, true, true);
        emit UserAccessSet(address(0), "MINTER", false);

        mt.setPublicMinting(false);

        assertTrue(!mt.publicMinting());
    }
}

contract ShowtimeMTMintingTest is DSTest, ERC1155Holder {
    User internal admin;
    ShowtimeMT internal mt;
    Hevm internal constant hevm = Hevm(HEVM_ADDRESS);

    function setUp() public {
        admin = new User();

        mt = new ShowtimeMT();
        mt.setAdmin(address(admin), true);
    }

    // it minter should be able to mint
    function testMintersCanMint() public {
        mt.issueToken(address(admin), 10, "some-hash", "0", address(0), 0);
        assertEq(mt.balanceOf(address(admin), 1), 10);

        // assert no royalty
        (address receiver, uint256 royaltyAmount) = mt.royaltyInfo(1, 1 wei);
        assertEq(receiver, address(0));
        assertEq(royaltyAmount, 0);

        assertEq(mt.uri(1), "https://gateway.pinata.cloud/ipfs/some-hash");
    }

    // it minter should be able to batch mint
    function testMintersCanBatchMint() public {
        uint256[] memory tokenAmounts = new uint256[](5);
        string[] memory hashes = new string[](5);
        address[] memory addresses = new address[](5);
        uint256[] memory royalties = new uint256[](5);

        for (uint256 i = 0; i < 5; i++) {
            tokenAmounts[i] = i;
            hashes[i] = string(abi.encodePacked("hash-", i));
            addresses[i] = address(0);
            royalties[i] = 0;
        }

        uint256[] memory tokenIds = mt.issueTokenBatch(address(admin), tokenAmounts, hashes, "0", addresses, royalties);

        for (uint256 i = 0; i < 5; i++) {
            assertEq(mt.balanceOf(address(admin), tokenIds[i]), tokenAmounts[i]);

            // assert no royalty
            (address receiver, uint256 royaltyAmount) = mt.royaltyInfo(tokenIds[i], 1 wei);
            assertEq(receiver, address(0));
            assertEq(royaltyAmount, 0);

            assertEq(mt.uri(tokenIds[i]), string(abi.encodePacked("https://gateway.pinata.cloud/ipfs/", hashes[i])));
        }
    }

    // it should not be able to mint if public minting is disabled
    function testMintersCantMintIfMintingIsDisabled() public {
        hevm.expectRevert("AccessProtected: caller is not minter");
        hevm.prank(address(0x1));

        mt.issueToken(address(0x1), 10, "some-hash", "0", address(0), 0);
    }
}

contract ShowtimeMTBurningTest is DSTest, ERC1155Holder {
    ShowtimeMT internal mt;
    Hevm internal constant hevm = Hevm(HEVM_ADDRESS);

    function setUp() public {
        mt = new ShowtimeMT();
    }

    // it holder should be able to burn
    function testHolderCanBurn() public {
        mt.issueToken(address(this), 10, "some-hash", "0", address(0), 0);
        assertEq(mt.balanceOf(address(this), 1), 10);

        mt.burn(address(this), 1, 10);
        assertEq(mt.balanceOf(address(this), 1), 0);
    }

    // it holder should be able to batch burn
    function testHolderCanBatchBurn() public {
        uint256[] memory tokenAmounts = new uint256[](5);
        string[] memory hashes = new string[](5);
        address[] memory addresses = new address[](5);
        uint256[] memory royalties = new uint256[](5);

        for (uint256 i = 0; i < 5; i++) {
            tokenAmounts[i] = i;
            hashes[i] = string(abi.encodePacked("hash-", i));
            addresses[i] = address(0);
            royalties[i] = 0;
        }
        uint256[] memory tokenIds = mt.issueTokenBatch(address(this), tokenAmounts, hashes, "0", addresses, royalties);

        for (uint256 i = 0; i < 5; i++) {
            assertEq(mt.balanceOf(address(this), tokenIds[i]), tokenAmounts[i]);
        }

        mt.burnBatch(address(this), tokenIds, tokenAmounts);

        for (uint256 i = 0; i < 5; i++) {
            assertEq(mt.balanceOf(address(this), tokenIds[i]), 0);
        }
    }

    // it can burn arbitrary amount
    function testArbitraryAmountsCanBeBurned() public {
        mt.issueToken(address(this), 10, "some-hash", "0", address(0), 0);
        assertEq(mt.balanceOf(address(this), 1), 10);

        mt.burn(address(this), 1, 5);
        assertEq(mt.balanceOf(address(this), 1), 5);
    }

    // it reverts of attempting to burn unowned tokens
    function testCannotBurnSomeonesTokens() public {
        mt.issueToken(address(0x1), 10, "some-hash", "0", address(0), 0);

        hevm.expectRevert("ERC1155: caller is not owner nor approved");
        mt.burn(address(0x1), 1, 10);
    }

    // it reverts on attempting to batch burn unowned tokens
    function testCannotBatchBurnSomeonesTokens() public {
        uint256[] memory tokenAmounts = new uint256[](5);
        string[] memory hashes = new string[](5);
        address[] memory addresses = new address[](5);
        uint256[] memory royalties = new uint256[](5);

        for (uint256 i = 0; i < 5; i++) {
            tokenAmounts[i] = i;
            hashes[i] = string(abi.encodePacked("hash-", i));
            addresses[i] = address(0);
            royalties[i] = 0;
        }
        uint256[] memory tokenIds = mt.issueTokenBatch(address(0x1), tokenAmounts, hashes, "0", addresses, royalties);

        hevm.expectRevert("ERC1155: caller is not owner nor approved");
        mt.burnBatch(address(0x1), tokenIds, tokenAmounts);
    }

    // it reverts on attempting to burn more than balance
    function testCannotBurnMoreThanOwned() public {
        mt.issueToken(address(this), 10, "some-hash", "0", address(0), 0);

        hevm.expectRevert("ERC1155: burn amount exceeds balance");
        mt.burn(address(this), 1, 11);
    }
}

contract ShowtimeMTURITest is DSTest, ERC1155Holder {
    ShowtimeMT internal mt;
    uint256 internal tokenId;
    Hevm internal constant hevm = Hevm(HEVM_ADDRESS);

    function setUp() public {
        mt = new ShowtimeMT();

        tokenId = mt.issueToken(address(this), 10, "some-hash", "0", address(0), 0);
    }

    // it should be able to get baseURI
    function testCanGetBaseURI() public {
        assertEq(mt.baseURI(), "https://gateway.pinata.cloud/ipfs/");
    }

    // it owner should be able to set baseURI
    function testOwnerCanSetBaseURI() public {
        mt.setBaseURI("https://gateway.test.com/ipfs/");

        assertEq(mt.baseURI(), "https://gateway.test.com/ipfs/");
    }

    // it should be able to get token URI
    function testCanGetTokenURI() public {
        assertEq(mt.uri(tokenId), "https://gateway.pinata.cloud/ipfs/some-hash");
    }
}

contract ShowtimeMTTransferTest is DSTest, ERC1155Holder {
    ShowtimeMT internal mt;
    uint256 internal tokenId;
    Hevm internal constant hevm = Hevm(HEVM_ADDRESS);

    function setUp() public {
        mt = new ShowtimeMT();

        tokenId = mt.issueToken(address(this), 10, "some-hash", "0", address(0), 0);
    }

    // it nft-owner should be able to transfer amount
    function testCanTransfer() public {
        uint256 fromBalance = mt.balanceOf(address(this), tokenId);
        uint256 toBalance = mt.balanceOf(address(0x1), tokenId);

        mt.safeTransferFrom(address(this), address(0x1), tokenId, 1, "0");

        uint256 fromBalance_new = mt.balanceOf(address(this), tokenId);
        uint256 toBalance_new = mt.balanceOf(address(0x1), tokenId);
        assertEq(fromBalance_new, fromBalance - 1);
        assertEq(toBalance_new, toBalance + 1);
    }
}

contract ShowtimeMTRoyaltyTest is DSTest, ERC1155Holder {
    ShowtimeMT internal mt;
    Hevm internal constant hevm = Hevm(HEVM_ADDRESS);

    function setUp() public {
        mt = new ShowtimeMT();
    }

    // it mints token with royalty
    function testMintsWithRoyalty() public {
        uint256 tokenId = mt.issueToken(address(this), 10, "some-hash", "0", address(0x1), 10_00);
        (address receiver, uint256 royaltyAmount) = mt.royaltyInfo(tokenId, 100); // 100 is sale price

        assertEq(receiver, address(0x1));
        assertEq(royaltyAmount, 10);
    }

    // it mints in batch with royalty
    function testBatchMintsWithRoyalty() public {
        uint256[] memory tokenAmounts = new uint256[](5);
        string[] memory hashes = new string[](5);
        address[] memory addresses = new address[](5);
        uint256[] memory royalties = new uint256[](5);

        for (uint256 i = 0; i < 5; i++) {
            tokenAmounts[i] = i;
            hashes[i] = string(abi.encodePacked("hash-", i));
            addresses[i] = address(0x1);
            royalties[i] = 500 * (i + 1);
        }
        uint256[] memory tokenIds = mt.issueTokenBatch(address(this), tokenAmounts, hashes, "0", addresses, royalties);
        for (uint256 i = 0; i < 5; i++) {
            (address receiver, uint256 royaltyAmount) = mt.royaltyInfo(tokenIds[i], 100);
            assertEq(receiver, address(0x1));
            assertEq(royaltyAmount, 5 * (i + 1));
        }
    }

    // it throws on % greater than 100%
    function testCannotHaveMoreThan100PercentRoyalty() public {
        hevm.expectRevert("ERC2981Royalties: value too high");
        mt.issueToken(address(this), 10, "some-hash", "0", address(this), 101_00);
    }
}

contract ShowtimeMTERC165Test is DSTest, ERC1155Holder {
    ShowtimeMT internal mt;
    Hevm internal constant hevm = Hevm(HEVM_ADDRESS);

    function setUp() public {
        mt = new ShowtimeMT();
    }

    // it supports interface IERC165
    function testIERC165Support() public {
        assertTrue(mt.supportsInterface(0x01ffc9a7));
    }

    // it supports interface IERC1155
    function testIERC1155Support() public {
        assertTrue(mt.supportsInterface(0xd9b67a26));
    }

    // it supports interface IERC2981
    function testIERC2981Support() public {
        assertTrue(mt.supportsInterface(0x2a55205a));
    }

    // it returns false for 0xffffffff
    function testReturnsFalseForFFF() public {
        assertTrue(!mt.supportsInterface(0xffffffff));
    }
}

src/test/ShowtimeBakeSaleTest.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "./Hevm.sol";
import "./TestToken.sol";
import "../../lib/ds-test/src/test.sol";
import "../ShowtimeMT.sol";
import "../ShowtimeV1Market.sol";
import "../periphery/ShowtimeBakeSale.sol";

import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";

contract User is ERC1155Holder {}

contract ShowtimeBakeSaleTest is DSTest, ERC1155Holder {
    uint256 constant INITIAL_NFT_SUPPLY = 1000;
    address constant BURN_ADDRESS = address(0xdEaD);
    address constant FORWARDER_ADDRESS = BURN_ADDRESS;

    User internal bob = new User();
    User internal alice = new User();
    User internal charity = new User();
    TestToken internal token;
    ShowtimeMT internal showtimeNFT;
    ShowtimeV1Market internal market;
    Hevm internal constant hevm = Hevm(HEVM_ADDRESS);

    address[] justCharityPayees = [address(charity)];
    uint256[] just100Shares = [100];

    ShowtimeBakeSale charitySeller;

    uint256 tokenId0PctRoyalty;
    uint256 tokenId10PctRoyaltyToAlice;
    uint256 tokenId100PctRoyaltyToAlice;
    uint256 tokenId10PctRoyaltyToZeroAddress;

    event ListingCreated(uint256 indexed listingId, address indexed seller, uint256 indexed tokenId);
    event ListingDeleted(uint256 indexed listingId, address indexed seller);
    event SaleCompleted(
        uint256 indexed listingId,
        address indexed seller,
        address indexed buyer,
        address receiver,
        uint256 quantity
    );
    event RoyaltyPaid(address indexed receiver, IERC20 currency, uint256 amount);
    event TransferSingle(address indexed operator, address indexed from, address indexed to, uint256 id, uint256 value);

    function setUp() public {
        // mint NFTs
        showtimeNFT = new ShowtimeMT();
        tokenId0PctRoyalty = showtimeNFT.issueToken(
            address(alice),
            INITIAL_NFT_SUPPLY,
            "some-hash",
            "0",
            address(0),
            0
        ); // 0% royalties
        tokenId10PctRoyaltyToAlice = showtimeNFT.issueToken(
            address(alice),
            INITIAL_NFT_SUPPLY,
            "some-hash",
            "0",
            address(alice),
            10_00
        ); // 10% royalties
        tokenId100PctRoyaltyToAlice = showtimeNFT.issueToken(
            address(alice),
            INITIAL_NFT_SUPPLY,
            "some-hash",
            "0",
            address(alice),
            100_00
        ); // 100% royalties

        // mint erc20s to bob
        token = new TestToken();
        hevm.prank(address(bob));
        token.mint(0xffffffff);

        address[] memory tokens = new address[](1);
        tokens[0] = address(token);

        // approvals
        market = new ShowtimeV1Market(address(showtimeNFT), FORWARDER_ADDRESS, tokens);
        hevm.prank(address(bob));
        token.approve(address(market), type(uint256).max);

        // deploy the normal splitter with 100% to charity
        charitySeller = new ShowtimeBakeSale(address(showtimeNFT), address(market), justCharityPayees, just100Shares);
    }

    function testDeploySalesContractWithNoPayees() public {
        hevm.expectRevert("PaymentSplitter: no payees");

        address[] memory payees = new address[](0);
        uint256[] memory shares = new uint256[](0);

        new ShowtimeBakeSale(address(showtimeNFT), address(market), payees, shares);
    }

    function testDeploySalesContractWithBadShares() public {
        hevm.expectRevert("PaymentSplitter: shares are 0");

        address[] memory payees = new address[](1);
        payees[0] = address(bob);

        uint256[] memory shares = new uint256[](1);
        shares[0] = 0;

        new ShowtimeBakeSale(address(showtimeNFT), address(market), payees, shares);
    }

    function testDeploySalesContractWithPayeesSharesMismatch() public {
        hevm.expectRevert("PaymentSplitter: payees and shares length mismatch");

        address[] memory payees = new address[](2);
        payees[0] = address(alice);
        payees[1] = address(bob);

        uint256[] memory shares = new uint256[](1);
        shares[0] = 100;

        new ShowtimeBakeSale(address(showtimeNFT), address(market), payees, shares);
    }

    function testDeploySalesContractWithDuplicatePayees() public {
        hevm.expectRevert("PaymentSplitter: account already has shares");

        address[] memory payees = new address[](2);
        payees[0] = address(bob);
        payees[1] = address(bob);

        uint256[] memory shares = new uint256[](2);
        shares[0] = 50;
        shares[1] = 50;

        new ShowtimeBakeSale(address(showtimeNFT), address(market), payees, shares);
    }

    function testOnlyOwnerCanCreateSales() public {
        hevm.expectRevert("Ownable: caller is not the owner");

        hevm.prank(address(alice));
        charitySeller.createSale(2, 2, 2, address(token));
    }

    function testOnlyOwnerCanCancelSales() public {
        hevm.expectRevert("Ownable: caller is not the owner");

        hevm.prank(address(alice));
        charitySeller.cancelSale(0);
    }

    function testOnlyOwnerCanWithdraw() public {
        hevm.expectRevert("Ownable: caller is not the owner");
        hevm.prank(address(alice));
        charitySeller.withdraw(42, address(alice));
    }

    function testHappyPathEndToEnd(uint16 price, uint8 quantity) public {
        uint256 tokenId = tokenId10PctRoyaltyToAlice;

        // when alice transfers her NFTs to the charitySeller
        hevm.prank(address(alice));
        showtimeNFT.safeTransferFrom(address(alice), address(charitySeller), tokenId, INITIAL_NFT_SUPPLY, "");

        // then we see the balance of the charitySeller reflected
        assertEq(INITIAL_NFT_SUPPLY, showtimeNFT.balanceOf(address(charitySeller), tokenId));

        // when the charitySeller puts the NFT up for sale
        uint256 listingId = charitySeller.createSale(tokenId, INITIAL_NFT_SUPPLY, price, address(token));

        // then we see the expected supply available for purchase
        assertEq(market.availableForSale(listingId), INITIAL_NFT_SUPPLY);

        if (quantity == 0) {
            // we can't purchase 0, it will throw `NullQuantity()`
            return;
        }

        // when bob purchases an NFT
        hevm.expectEmit(true, true, true, true);
        emit SaleCompleted(listingId, address(charitySeller), address(bob), address(bob), quantity);

        hevm.prank(address(bob));
        market.buy(listingId, tokenId, quantity, price, address(token), address(bob));

        // then we see the balances reflected
        uint256 salePrice = uint256(quantity) * uint256(price);
        uint256 royalties = salePrice / 10;
        uint256 saleProceeds = salePrice - royalties;
        uint remainingSupply = INITIAL_NFT_SUPPLY - quantity;
        assertEq(quantity, showtimeNFT.balanceOf(address(bob), tokenId));
        assertEq(remainingSupply, showtimeNFT.balanceOf(address(charitySeller), tokenId));
        assertEq(saleProceeds, token.balanceOf(address(charitySeller)));
        assertEq(royalties, token.balanceOf(address(alice)));

        if (price == 0) {
            // nothing to release, it will throw `account is not due payment`
            return;
        }

        // when someone calls release (could be anyone, so let's randomly pick bob)
        hevm.prank(address(bob));
        charitySeller.release(token, address(charity));

        // then we see the balance of the sales contract going to the charity
        assertEq(0, token.balanceOf(address(charitySeller)));
        assertEq(saleProceeds, token.balanceOf(address(charity)));

        // when the deployer cancels the sale, then the listing really is deleted
        hevm.expectEmit(true, true, true, true);
        emit ListingDeleted(listingId, address(charitySeller));
        charitySeller.cancelSale(listingId);

        // when the deployer burns the remaining supply
        hevm.expectEmit(true, true, true, true);
        emit TransferSingle(
            address(charitySeller),
            address(charitySeller),
            address(alice),
            tokenId,
            remainingSupply
        );
        charitySeller.withdraw(tokenId, address(alice));

        // then the transfer really happened
        assertEq(0, showtimeNFT.balanceOf(address(charitySeller), tokenId));
        assertEq(remainingSupply, showtimeNFT.balanceOf(address(alice), tokenId));
    }
}

